const INITIAL_VALUE = {
    authData:  null
}

const auth = (state = INITIAL_VALUE, action) => {
    switch (action.type) {
        case "AUTH":
            localStorage.setItem("profileInfo", JSON.stringify({...action?.payload}))
            return state
        default:
            return state
    }
}

export default auth;
