import {fetchPosts, createPost, updatePost, deletePost} from "../../Api/api"

 const getPost = () => async (dispatch) =>  {
    try{
        const {data} = await fetchPosts()
        dispatch({type: "FETCH_ALL" , payload: data})
    }catch(error){
        console.log(error)
    }
}
export default getPost


export const createNewPost = (post) => async (dispatch) => {

    try{
        const {data} = await createPost(post);
        console.log("this is",data);
        dispatch({type: "CREATE_POST",  payload: data})
    }catch(error){
        console.log(error)
    }
}


export const updateNewPost = (id, receivedPostData) => async (dispatch) => {

    try{
      const {data}  =  await updatePost(id, receivedPostData)

      dispatch({type: "UPDATE_POST", payload: data})

    }catch(error){
        console.log(error)
    }

}
export const deleteThePost = (id) =>  async (dispatch) => {
    try{

        await deletePost(id)

        dispatch({type: "DELETE" , payload: id})

    }catch(error){
        console.log(error)
    }

}
