import axios from 'axios';

export const fetchPosts = () => axios.get("http://localhost:5000/posts")
export const createPost = (post) => axios.post("http://localhost:5000/posts", post)
export const updatePost = (id, receivedPostData) => axios.patch(`http://localhost:5000/posts/${id}`, receivedPostData)
export const deletePost = (id) => axios.delete(`http://localhost:5000/posts/${id}`)
export const likePost   = (id) => axios.patch(`http://localhost:5000/posts/${id}/likePost`)
export const signIn = (inputData) => axios.post("http://localhost:5000/users/signin", inputData)
export const signUp = (inputData) => axios.post("http://localhost:5000/users/signup", inputData)
