import { useEffect } from 'react';
import './App.css';
import {useDispatch} from "react-redux"
import getPost from './REDUX/action/action';
import Navbar from "./components/Nav Panel/Navbar"
import Posts from './components/Post/posts'
import FormElements from './components/Form/forms';

const App = () => {

  const dispatch = useDispatch();

  //MAKES DISPATCHING WHERE IT CAN DISPLAY OUR DATA ON THE SCREEN
  // IT WILL WORK ONLY WHEN THE DISPATCH METHOD WORKS

    useEffect(() => {
      dispatch(getPost())
    },[dispatch])

  return (
    <div className="App">
      <Navbar/>
      <div className="container">
      <Posts/>
      <FormElements/>
      </div>
    </div>
  );
}

export default App;
