import React, {useEffect} from 'react'
import {Paper, Typography, CircularProgress, Divider} from "@material-ui/core"
import {useParams, useHistory} from "react-router-dom"
import {useDispatch, useSelector} from "react-redux"
import { getSinglePost } from '../../REDUX/action/action'
import useStyles from "./styles"
import "./details.css"

const PostDetails = () => {
    const posts = useSelector(state => state.posts) //Taking all posts from reducerb
    const {post} = useSelector(state => state.posts)
    console.log(post)
    const classes = useStyles()
    const dispatch = useDispatch()
    const history = useHistory()
    const {id} = useParams()

    useEffect(()=> {
        dispatch(getSinglePost(id))
    }, [id]) //Executing by changing id
      //CHECKING THE EXISTENCE OF SINGLE POST

      if(!post) return null;

    return (
        <div>
            {!post.length ? <CircularProgress size={1}/> : (
                <div>
                      <Paper style={{ padding: '20px', borderRadius: '15px' }} elevation={6}>
                           <div className={classes.card}>
                             <div className={classes.section}>
                               <Typography variant="h3" component="h2">{post.title}</Typography>
                               <Typography gutterBottom variant="h6" color="textSecondary" component="h2">{post.tags.map((tag) => `#${tag} `)}</Typography>
                               <Typography gutterBottom variant="body1" component="p">{post.message}</Typography>
                               <Divider style={{ margin: '20px 0' }} />
                               <Divider style={{ margin: '20px 0' }} />
                             </div>
                             <div className={classes.imageSection}>
                               <img className={classes.media} src={post.selectedFile || 'https://user-images.githubusercontent.com/194400/49531010-48dad180-f8b1-11e8-8d89-1e61320e1d82.png'} alt={post.title} />
                             </div>
                           </div>
                         </Paper>
                      )
                </div>

            )}
        </div>
    )
}

export default PostDetails
