import React from 'react'
import {Card, CardBody, CardText, CardImg, CardTitle, CardSubtitle, Button} from "reactstrap"
import "./post.css"


//Adding cards and specified by props coming from posts as a selector
//Styling with reactstrap
const Post = ({post}) =>{
    return (
        <Card>
        <CardImg top width="auto" height="320px" src={post.selectedFile} alt="Card image cap" />
        <CardTitle tag="h5" className="creator">{post.creator}</CardTitle>
        <CardBody>
          <CardTitle tag="h3">{post.title}</CardTitle>
          <CardSubtitle tag="h7" className="mb-2 text-muted">{post.tags.map(tag => `#${tag} `)}</CardSubtitle>
          <CardText>{post.message}</CardText>
          <Button variant="blue">Like</Button>
        </CardBody>
      </Card>
    )
}

export default Post
